using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;	

public class Program
{	
	public static void Main()
	{
		string input = Console.ReadLine();
		var Ray = input.Split(' ');

		int size = GetLeng(input);
		int wi = size/4;

		foreach (string chunk in Ray)
		{
			int sizeChar = (int)(chunk[0] - '0');
			int sizeBlanks = (int)(chunk[2] - '0');
			char ch = chunk[1];
			for (int row = 0 ; row < sizeChar ; row++)
			{
				Console.WriteLine(new string(ch, wi));
			}

			for (int row = 0 ; row < sizeBlanks ; row++)
			{
				Console.WriteLine(new string(' ', wi));
			}			
		}
	}

	public static int GetLeng(string input)
	{        
        List<string> list = input.Trim().Split(' ').ToList();
		int leng = 0;
		foreach (string chunk in list)
		{
			int sizeChar = (int)(chunk[0] - '0');
			int sizeBlanks = (int)(chunk[2] - '0');
			leng += sizeChar + sizeBlanks;
		}
		return leng;
	}
}
