import java.util.*

fun main(args : Array<String>) {
    val input = Scanner(System.`in`)
    val H = input.nextInt()

    (1..H).forEach { index ->
        printLine(index, H)
    }
    ((H-1).downTo(1)).forEach { index ->
        printLine(index, H)
    }
}

fun printLine(index: Int, height: Int) {
    val line = ('A'..('A' + index - 1)).joinToString("")
    println(
        " ".repeat(height - index) + 
        line +
        line.reversed().substring(1)
    )
}
