const N = parseInt(readline());
let object = {};
for (let i = 0; i < N; i++) {
    const line = readline().split(" ");
    let name = line.shift();
    if(name in object){
        object[name].push(...line);
    } else {
        object[name] = line
    }
}
console.log(JSON.stringify(object));
