import java.util.*

fun main(args : Array<String>) {
    val input = Scanner(System.`in`)
    val N = input.nextInt()
    if (input.hasNextLine()) {
        input.nextLine()
    }
    val grid = Grid(N, input)

    repeat(N) { y -> 
        repeat(N) { x ->
            if (grid.isOffOrCross(x, y)) {
                val found = listOf(x to y+1, x to y-1, x+1 to y, x-1 to y )
                    .all { (neighborX, neighborY) -> grid.isOffOrCross(neighborX, neighborY)}
                if (found) {
                    println("$x $y")
                }
            }
        }
    }
}

class Grid(private val N: Int, input: Scanner) {
    private val fields = Array(N) { input.nextLine().toCharArray() }

    fun isOffOrCross(x: Int, y: Int): Boolean =
        x < 0 || x >= N || y < 0 || y >= N || fields[y][x] == 'x'
}
