enum Color {
	WHITE (0,"WHITE"), RED(1,"RED"), BLUE(2,"BLUE"), YELLOW(3,"YELLOW"), GREEN(4,"GREEN"), MULTICOLOR(5,"MULTICOLOR"), NONE(6,"-");

	private final int id;
    private final String name;

    private Color(int val, String s) {
    	id=val;
        name = s;
    }

    public String toString()
    {
    	return name;
    }

    public int index()
    {
    	return id;
    }

    public static Color findColor(String s)
    {
    	Color co=Color.NONE;
		if (s.compareTo("WHITE")==0) co=Color.WHITE;
		else if (s.compareTo("RED")==0) co=Color.RED;
		else if (s.compareTo("BLUE")==0) co=Color.BLUE;
		else if (s.compareTo("YELLOW")==0) co=Color.YELLOW;
		else if (s.compareTo("GREEN")==0) co=Color.GREEN;
	    return co;
    }
}

class Card {
	Color co;
	int level;

	Card(Color co, int level)
	{
		this.co=co;
		this.level=level;
	}

	public String toString()
	{
		return co+"-"+level;
	}
}

