import Foundation

let N = Int(readLine()!)!
var totalArea: Double = 0.0

for _ in 1...N {
  let inputs = readLine()!.split(separator: " ")
  let shapeType = String(inputs[0])
  let a = Double(inputs[1])!
  let b = Double(inputs[2])!
  var area: Double = 0.0

  if shapeType == "C" {
    area = Double.pi * a * a
  } else if shapeType == "R" {
    area = a * b
  }

  totalArea += area
}

print(String(format: "%.2f", totalArea))
