#include <stdio.h>

int main()
{
    long long int N, S;
    scanf("%lld%lld", &N, &S); fgetc(stdin);
    char D[21];
    fgets(D, 21, stdin);
    long long int i = 1;

    while (i<=N)
        i*=2;
    i=(N-i/2)*2+S;
    if(D[0]=='R')
        i=N-i+2*S;
    printf("%lld\n", i>N?i-N:i<0?i+N:i);
    return 0;
}
