import java.util.*;
import java.io.*;
import java.math.*;

class Solution {
    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int W = in.nextInt();
        int H = in.nextInt();
        if (in.hasNextLine()) {
            in.nextLine();
        }
        double area = 0;
        for (int i = 0; i < H; i++) {
            String row = in.nextLine();
            for (char c : row.toCharArray()) {
                if (c == '.') {
                    continue;
                }
                if (c == 'X') {
                    area += 1.0;
                } else {
                    area += 0.5;
                }
            }
        }
        double ratio = area / W / H;
        // Rectangle: 1.0
        // Triangle: 0.5
        // Ellipse: pi/4 = 0.7854
        System.out.println(ratio > 0.8927 ? "Rectangle" : (ratio > 0.6427 ? "Ellipse" : "Triangle"));
    }
}
