const line = readline().split(" ");
let balanced = true;
let stack = [];
line.forEach((x) => {
    if (x[0] == "_") {
        stack.push(x);
    } else if (x[x.length - 1] == "_") {
        let reversed = x.split("").reverse().join("");
        if (stack.pop() != reversed) {
            balanced = false;
        }
    }
});
if (stack.length > 0) {
    balanced = false;
}
console.log(balanced ? "Balanced" : "Not Balanced");
