using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;	

public class Program
{
	public static void Main()
	{
		string input = Console.ReadLine();
		var inputRay = input.Split(' ');
		string name = inputRay[0];
		int cAge = int.Parse(inputRay[2]);
		int ans = 0;
		if (cAge == 0)
		{
			Console.WriteLine(name + " is just a little baby");	
			return;
		}
		if (cAge >= 1) ans = 15;
		if (cAge >= 2) ans = 24;
		if (cAge >= 3) 
		{
			int addlYears = cAge - 2;
			ans = 24 + addlYears * 4;
		}
		Console.WriteLine(name + " is like a " + ans + "-year-old person");
	}
}
/*
A cat reaches the approximate human age of 15 during its first year, then 24 at age two. Each year after, 
it ages approximately four "cat years" for every calendar year. This means that a five-year-old feline would be approximately 36 in cat years. 
*/
