using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;	

public class Program
{
	public const string bl = " "; 
	public static void Main()
	{
		string clue = Console.ReadLine();       

        AlphaModel.ListOfAMs = new List<AlphaModel>();

		string raw0 = "Authority, Bills, Capture, Destroy, Englishmen, Fractious, Galloping, High, Invariably, Juggling, Knights, Loose, Managing, Never, Owners, Play, Queen, Remarks, Support, The, Unless, Vindictive, When, Xpeditiously, Your, Zigzag";
		string raw1 = "Apples, Butter, Charlie, Duff, Edward, Freddy, George, Harry, Ink, Johnnie, King, London, Monkey, Nuts, Orange, Pudding, Queenie, Robert, Sugar, Tommy, Uncle, Vinegar, Willie, Xerxes, Yellow, Zebra";
		string raw2 = "Amsterdam, Baltimore, Casablanca, Denmark, Edison, Florida, Gallipoli, Havana, Italia, Jerusalem, Kilogramme, Liverpool, Madagascar, New-York, Oslo, Paris, Quebec, Roma, Santiago, Tripoli, Uppsala, Valencia, Washington, Xanthippe, Yokohama, Zurich";
		string raw3 = "Alfa, Bravo, Charlie, Delta, Echo, Foxtrot, Golf, Hotel, India, Juliett, Kilo, Lima, Mike, November, Oscar, Papa, Quebec, Romeo, Sierra, Tango, Uniform, Victor, Whiskey, X-ray, Yankee, Zulu";
		new AlphaModel(raw0); new AlphaModel(raw1); new AlphaModel(raw2); new AlphaModel(raw3);

		AlphaModel.FindInputAlpha(clue);
		AlphaModel.FindOutputAlpha();

		string ans = "";
		var clueRay = clue.Split(' ');
		foreach (string word in clueRay)
		{
			char ch = word[0];
			int i = (int)(ch) - 65;
			string newWord = AlphaModel.OutputAlpha.Ray[i];
			ans += newWord + bl;
		}
		ans = ans.Trim();
		Console.WriteLine(ans);
	}

	public class AlphaModel
	{
		public int Index;
		public string RawString;
		public string[] Ray;
		public static List<AlphaModel> ListOfAMs = new List<AlphaModel>();
		public static AlphaModel InputAlpha;
		public static AlphaModel OutputAlpha;
		public static int NextIndex = 0;

		public AlphaModel(string rawString)
		{
			Index = NextIndex++;
			RawString = rawString;
			Ray = RawString.Replace(", ",",").Split(',');
			ListOfAMs.Add(this);
		}

		public static void FindInputAlpha(string input)
		{
			var inputRay = input.Split(' ');
			foreach (AlphaModel a in ListOfAMs)
			{
				bool isInput = true;  // this alphabet is the input (assume) ... now prove otherwise
				foreach (string word in inputRay)
				{
					if (a.Ray.Contains(word) == false) // the word IS NOT somwhere in this alphabet...
					{
						isInput = false;               // ...so it can't be the input
						break;                         // (so no need to check any further)
					}
				}
				if (isInput == true) 
				{
					InputAlpha = a;
					return;
				}
			}
		}

		public static void FindOutputAlpha()
		{
			int leng = ListOfAMs.Count();
			int i = InputAlpha.Index ;
			i++;
			if (i == leng) i = 0;
			OutputAlpha = ListOfAMs[i];
		}

