using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution
{
    static void Main(string[] args)
    {
        string s = Console.ReadLine()+";";
        int res = 0;
        int current = 0;
        char prevOp = '$';
        foreach(char c in s)
        {
            if(char.IsDigit(c))
            {
                current = current*10+int.Parse(c.ToString());
            }
            else
            {
                switch(prevOp)
                {
                    case '$':
                        res = current;
                    break;
                    case '+':
                        res += current;
                    break;
                    case '-':
                        res -= current;
                    break;
                    case '*':
                        res *= current;
                    break;
                    case '/':
                        res /= current;
                    break;
                }
                prevOp = c;
                current = 0;
            }
        }

        Console.WriteLine(res);
    }
}
