#include <cstdio>
#include <cstdlib>
#include <cstring>
#include <list>
#include <algorithm>

using namespace std;

#define N       4
#define N_2     (N*N)
#define poss    ((1<<N_2)-1)
#define base    'A'
#define vide    '.'
#define bx(j, k) (((int)((j) / N)) * N + (int)((k) / N))


unsigned candrow[N_2];
unsigned candcol[N_2];
unsigned candbox[N_2];
char grid[N_2][N_2+1];
char solution[N_2][N_2+1];

bool backtrack(list<unsigned>& pos) {
    if (pos.empty()) {
        return true;
    }

    unsigned mini = 2 * N_2;
    list<unsigned>::const_iterator cit;
    for (auto it=pos.begin(); it!=pos.end(); ++it) {
        const unsigned p = *it;
        const unsigned i = p / N_2;
        const unsigned j = p % N_2;
        const unsigned b = bx(i, j);
        const unsigned nb = __builtin_popcount(candrow[i] & candcol[j] & candbox[b]);
        if (nb < mini) {
            mini = nb;
            cit = it;
        }
    }

    const unsigned p = *cit;
    const unsigned i = p / N_2;
    const unsigned j = p % N_2;
    const unsigned b = bx(i, j);
    unsigned bitset = candrow[i] & candcol[j] & candbox[b];
    pos.erase(cit);
    while (bitset) {
        const unsigned k = __builtin_ctz(bitset);
        bitset &= ~(1 << k);

        candrow[i] &= ~(1 << k);
        candcol[j] &= ~(1 << k);
        candbox[b] &= ~(1 << k);

        if (backtrack(pos)) {
            solution[i][j] = base + k; 
            return true;
        }

        candrow[i] |= (1 << k);
        candcol[j] |= (1 << k);
        candbox[b] |= (1 << k);
    }
    pos.push_front(p);
    return false;
}

void display (char g[N_2][N_2+1], FILE *stream)
{
    for (int i=0; i<N_2; i++)
    {
        fputs(g[i], stream);
        fputc('\n', stream);
    }
}

int main (void)
{
    fill(candrow, candrow+N_2, poss);
    fill(candcol, candcol+N_2, poss);
    fill(candbox, candbox+N_2, poss);

    for (unsigned l=0; l < N_2; l++)
    {
        char line[N_2 + 1];
        scanf("%[^\n]", line);
        fgetc(stdin);
        strncpy(grid[l], line, N_2 + 1 );
    }

    list<unsigned> pos;
    for (unsigned i=0, n=0; i<N_2; ++i)
        for (unsigned j=0; j<N_2; ++j, ++n) {
            const unsigned b = bx(i, j);
            const char val = grid[i][j];
            if (val != vide) {
                const unsigned k = val - base;
                candrow[i] &= ~(1 << k);
                candcol[j] &= ~(1 << k);
                candbox[b] &= ~(1 << k);
                solution[i][j] = val;
            } else {
                pos.push_back(n);
            }
        }

    //display(grid, stderr);

    bool ok = backtrack(pos);
    if (ok) display(solution, stdout);

    return 0;
}
