function solve(equation: string): string {
    const left = equation.substring(0, equation.indexOf('='));
    const right:number = eval(equation.substring(equation.indexOf('=')+1));
    if (eval(left) == right) {
        return equation;
    }
    for (let i = 0 ; i < left.length-2 ; i++) {
        for (let j = i+2 ; j <= left.length ; j++) {
            const solution = left.substring(0, i)+'('+left.substring(i, j)+')'+left.substring(j);
            try {
                if (right === eval(solution)) {
                    return solution+'='+right;
                }
            } catch (e) {
            }
        }
    }
    return equation;
}

