let n = parseInt(readline());
let yoda = readline().split(" ");

// swap case :
n = n%yoda.length;
yoda[0] = yoda[0][0].toLowerCase() + yoda[0].slice(1);
yoda[n] = yoda[n][0].toUpperCase() + yoda[n].slice(1);

// reverse :
console.log((yoda.slice(n)).concat(yoda.slice(0, n)).join(" "));
