lines = int(input())
shape = []
perimeter = 0
for i in range(lines):
    part = input()
    shape.append(part)
for i in shape[0]:
    if i == '-':
        perimeter += 1
    elif (i == '/') or (i == '\\'):
        perimeter += 2
    else:
        perimeter += 3
for i in range(1, lines - 1):
    if shape[i][0] == '-':
        perimeter += 1
    elif (shape[i][0] == '/') or (shape[i][0] == '\\'):
        perimeter += 2
    else:
        perimeter += 3
    if shape[i][-1] == '-':
        perimeter += 1
    elif (shape[i][-1] == '/') or (shape[i][-1] == '\\'):
        perimeter += 2
    else:
        perimeter += 3
for i in shape[-1]:
    if i == '-':
        perimeter += 1
    elif (i == '/') or (i == '\\'):
        perimeter += 2
    else:
        perimeter += 3
print(perimeter)
