words_amount = int(input())
words = []
vowels = 'aeiou'
for i in range(words_amount):
    word = input()
    words.append(word)
commands_amount = int(input())
for i in range(commands_amount):
    command, check = input().split()
    if command == 'start':
        words = list(filter(lambda x:x[:len(check)] == check, words))
    elif command == 'end':
        words = list(filter(lambda x:x[len(x) - len(check):] == check, words))
    elif command == 'vowels':
        for i in range(len(words) - 1, -1, -1):
            vowels_amount = 0
            for j in vowels:
                vowels_amount += words[i].count(j)
            if vowels_amount != int(check):
                words.pop(i)
    elif command == 'consonants':
        for i in range(len(words) - 1, -1, -1):
            consonants_amount = len(words[i])
            for j in vowels:
                consonants_amount -= words[i].count(j)
            if consonants_amount != int(check):
                words.pop(i)
if len(words) == 0:
    print('NONE')
else:
    print('\n'.join(words))
