// Get data.
var G = parseInt(readline());
var S = parseInt(readline());
var B = parseInt(readline());

// Convert the bronze coins into silver coins.
if (B >= 100)
{
    let r = parseInt(B / 100, 10);
    S += r;
    B -= r * 100;
}

// Convert the silver coins into gold coins.
if (S >= 100)
{
    let r = parseInt(S / 100, 10);
    G += r;
    S -= r * 100;
}

// Write the result.
print(G + 'G ' + S + 'S ' + B + 'B');
