name_1 = input()
name_2 = input()
n = int(input())
network=[]
for i in range(n):
    linked = input()
    network.append(linked)

def amis(name1,name2,network):
    tester = str(name1) +'->'+str(name2)
    for link in network:
        if link==tester:
            return(True)
    return(False)
part1 = amis(name_1,name_2,network)
part2 = amis(name_2,name_1,network)
if part1 and part2 : print("mutual friends")
elif part1 or part2 : print("unilateral friendship")
else : print("not friends")
