const S = readline();
const CHARS = readline();

let charsArr = CHARS.split(' ')
let result = 1;
let found = false;

for (let i = 0; i < charsArr.length; i++) {
	const charIndices = [];
	let j = S.indexOf(charsArr[i]);
	while (j > -1) {
        charIndices.push(j);
        j = S.indexOf(charsArr[i], j+1);
    }
	if (charIndices.length > 0) {
      result *= charIndices.reduce((a,c) => a*c);
      found = true;
    }
}
console.log(found ? result : "NONE");
