using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;	

public class Program
{
	public const string bl = " ";
	public static int DabCounter = 0;

	public static void Main()
	{		
        string junk = Console.ReadLine();
		Card aCard = new Card();

        for (int row = 0 ; row < 5 ; row++)
        {
            string aLine = Console.ReadLine();
            aLine = aLine.Trim().Replace("  "," ").Replace("  "," ");
            aLine = aLine.Replace("FR","-1");
            var strRay = aLine.Split(' ');
            for (int col = 0 ; col < 5 ; col++)
            {
                string strCell = strRay[col];
                int intCell = int.Parse(strCell);
                aCard.Ray[col,row] = intCell;
            } 
        }

        string input = Console.ReadLine();

		var strBalls = input.Split(' ');
		var intBalls = new List<int>();

		foreach (string strBall in strBalls)
		{	
			intBalls.Add(Bumber.RemoveLetter(strBall));
		}

		foreach (int ball in intBalls)
		{
			aCard.CallABall(ball);		
		}
        int ans = 24 - DabCounter;
        Console.WriteLine(ans);
	}

	public class Card
	{
		public int[,] Ray = new int[5,5];

		public void CallABall(int ball)
		{
			for (int whichCol = 0 ; whichCol < 5; whichCol++)
			{
				for (int whichRow = 0 ; whichRow < 5; whichRow++)
				{
					int thisCell = Ray[whichCol,whichRow] ;
					if (ball == thisCell) DabCounter++;
				}
			}
		}
	}

	public class Bumber // Bingo Number
	{
		public int NumberPart;

		public Bumber(int num)
		{
			NumberPart = num;
		}

		public static int RemoveLetter(string str) // use for Balls
		{
			string strNum = str.Substring(1);
			int i = int.Parse(strNum);
			return i;
		}
	}
}
