using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;	

public class Program
{
	public const string bl = " ";
	public static string ansRow = "Horizontal Bingo";
	public static string ansCol = "Vertical Bingo";
	public static string ansDiag = "Diagonal Bingo";
	public static string ansNone = "No Bingo yet";

	public static void Main()
	{		
        string header_junk = Console.ReadLine();

		Card aCard = new Card();

        for (int row = 0 ; row < 5 ; row++)
        {
            string aRow = Console.ReadLine();
            aRow= aRow.Trim();
            var RowRay = aRow.Split(' ');

            for (int col = 0 ; col < 5 ; col++)
            {
                aCard.Ray[col,row] =  int.Parse(RowRay[col]);
            }
        }

        string ballsInput = Console.ReadLine();
        string[] strBallsRay = ballsInput.Split(' ');
        int[] intsBallsRay = Array.ConvertAll(strBallsRay, s => int.Parse(s));

		foreach (int intBall in intsBallsRay)
		{
			aCard.CallABall(intBall);	
			aCard.CheckIfBingoYet();	
			if (aCard.BingoAchieved) return;
		} 
		Console.WriteLine(ansNone);
	}

	public class Card
	{
		public int[,] Ray = new int[5,5];
		public bool BingoAchieved = false; 

		public Card(){}

		public void CallABall(int ball)
		{
			for (int whichCol = 0 ; whichCol < 5; whichCol++)
			{
				for (int whichRow = 0 ; whichRow < 5; whichRow++)
				{
					int cell = Ray[whichCol,whichRow];
					if (ball == cell) Ray[whichCol,whichRow] = 0;;
				}
			}
		}

		public void CheckIfBingoYet() // meaning regular bingo so have to check rows and cols and crosses
		{
			bool bool1 = IsAnyColFullyDabbed();
			bool bool2 = IsAnyRowFullyDabbed();
			bool bool3 =  HowManyPartsOfCrossAreDabbed() > 0;
			bool bool123 = bool1 || bool2 || bool3;
			if (bool123)
			{
				BingoAchieved = true; 
			}
		}

		public bool IsAnyColFullyDabbed() // for regular bingo have to check rows and cols  **** COLS ****
		{
			for (int col = 0 ; col < 5 ; col++)
			{
				bool isThisColFullyDabbed = true;
				for (int row = 0 ; row < 5 ; row++)
				{
					if (Ray[col,row] != 0) isThisColFullyDabbed = false;
				}
				if (isThisColFullyDabbed) 
				{
					Console.WriteLine(ansCol);
					return true;
				}
			}		
			return false;
		}


		public bool IsAnyRowFullyDabbed() // for regular bingo have to check rows and cols  **** ROWS  ****
		{
			for (int row = 0 ; row < 5 ; row++)
			{
				bool isThisRowFullyDabbed = true;
				for (int col = 0 ; col < 5 ; col++)
				{
					if (Ray[col,row] != 0) isThisRowFullyDabbed = false;
				}
				if (isThisRowFullyDabbed) 
				{
					Console.WriteLine(ansRow);
					return true;
				}
			}		
			return false;
		}

		public int HowManyPartsOfCrossAreDabbed() // meaning regular bingo and cross bingo have to check Cross  **** CROSS ****
		{
			int count = 0;
			bool isCrossHalfAAAdabbed = true;   // this part of the X \
			bool isCrossHalfBBBdabbed = true;   // this part of the X /
			for (int row = 0 ; row < 5 ; row++)
			{
				int colAAA = row;     // this part of the X \
				int colBBB = 4-row;   // this part of the X /
				if (Ray[colAAA,row] != 0) isCrossHalfAAAdabbed = false;
				if (Ray[colBBB,row] != 0) isCrossHalfBBBdabbed = false;				
			}

			if (isCrossHalfAAAdabbed) count++;			
			if (isCrossHalfBBBdabbed) count++;
			if (count > 0)
			{
				Console.WriteLine(ansDiag);
				return count;
			}
			return count;
		}
	}
}
