using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;	

public class Program
{	
	public static void Main()
	{		
        decimal mil = 1000M * 1000M;
        decimal moneyNow = Convert.ToDecimal(Console.ReadLine());
        decimal annualRateOfReturn = Convert.ToDecimal(Console.ReadLine());
        decimal annualRateOfReturnPercent = annualRateOfReturn/100M;
        decimal annualGenieMoney = Convert.ToDecimal(Console.ReadLine());
        int yourAge = int.Parse(Console.ReadLine());


        while (moneyNow < mil)
        {
            // on your next birthday:
                yourAge++; 
                moneyNow += moneyNow * annualRateOfReturnPercent;
                moneyNow += annualGenieMoney;
        }

        Console.WriteLine(yourAge);
	}
}
