use 5.024;
use warnings;
use experimental 'smartmatch';
use List::Util qw( sum );

chomp( my $title = <> );
chomp( my $author = <> );
my ($friends,$foes) = <> =~ /\d+/g;
chomp( my @friends = map scalar <>, 1..$friends );
chomp( my @foes = map scalar <>, 1..$foes );

my $titleSum = sum (map ord, $title =~ /./g) & 0x7F;
my $sockSum = sum (map ord, "Contribution Revenge Sockpuppet" =~ /./g) & 0x7F;

my $goodwill;

if ( $author ~~ @friends ) { $goodwill = 1 }
elsif ( $author ~~ @foes ) { $goodwill = 0 }
else { $goodwill = $titleSum == $sockSum }

warn "friends: @friends\n";
warn "foes: @foes\n";
warn "$titleSum $sockSum\n";

print $goodwill ? 'ACCEPT' : 'REJECT';
