const herbs = readline()

let qtY = (herbs.match(/Y/g) || []).length
let qtR = (herbs.match(/R/g) || []).length
let qtG = (herbs.match(/G/g) || []).length

let ggg = 0
let gry = 0
let ggy = 0

if (qtY > 0 && qtR > 0 && qtG > 0){
    gry = Math.min(qtY, qtG, qtR)
    qtY -= gry
    qtR -= gry
    qtG -= gry
}

if (qtY > 0 && qtG > 1){
  ggy = Math.floor(Math.min(qtY, qtG / 2))
  qtY -= ggy
  qtG -= ggy * 2
}

if (qtG > 2){
  ggg = Math.floor(qtG / 3)
  qtG -= ggg * 3
}

console.log(gry, ggy, ggg, qtG)
