 * the standard input according to the problem statement.
 **/

const n = parseInt(readline());

 // Write an answer using console.log()
// To debug: console.error('Debug messages...');

const next = (m) => [...m.toString()].reduce((sum, digit) => sum + parseInt(digit)**2, 0);

let i = 1;
const sequence = [n, next(n)];

while (sequence[i] !== 1) {
    const nextOne = next(sequence[i]);

    const looping = sequence.includes(nextOne);

    sequence.push(nextOne);
    i++;

    if (looping) {
        break;
    }
}

console.log(...sequence.slice(1));

const isSad = (m) => [16, 37, 58, 89, 145, 42, 20, 4].includes(m);

console.log(isSad(sequence[i]) ? "sad" : i);
