import java.util.*

fun main() {
    val input = Scanner(System.`in`)
    val userData = List(input.nextLine().toInt()) { input.nextLine() }
        .map { pair -> pair.substringBefore(" ") to pair.substringAfter(" ") }
        .toMap()
    val regex = "<(\\w+)>".toRegex()
    var template = input.nextLine()
    while (template.contains("<")) {
        template = regex.replace(template) { userData[it.groupValues[1]] ?: "" }
    }
    println(template)
}
