w = int(input())
h = int(input())

if (w*h)%2:
	print(0)
	exit()

if w%2:
	w, h = h, w

initial_paving = [[None for _ in range(w)] for _ in range(h)]
for i in range(h):
	for j in range(0, w, 2):
		initial_paving[i][j] = 'L'
		initial_paving[i][j+1] = 'R'

def hash(paving):
	return (''.join(''.join(x) for x in paving))

done = set([hash(initial_paving)])
to_do = [(initial_paving, 0, 0)]

def coordinates_to_parse(last_i, last_j):
	for i in range(h-1):
		for j in range(w-1):
			if i>last_i-2 or j>last_j-2:
				yield i, j

def coordinates_to_parse(last_i, last_j):
    if last_i>0:
        for j in range(max(0, last_j-1),  w-1):
            yield last_i-1, j
    if last_i < h-1:
        for j in range(max(0, last_j-1), w-1):
                yield last_i, j
    for i in range(last_i+1, h-1):
        for j in range(w-1):
            yield i, j

while to_do:
	(paving, last_i, last_j), *to_do = to_do

	for i, j in coordinates_to_parse(last_i, last_j):
            if paving[i][j] == 'L' and paving[i][j+1] == 'R' and paving[i+1][j] == 'L' and paving[i+1][j+1] == 'R':
                new_p = [paving[k] if k not in (i, i+1) else paving[k][:] for k in range(h)]
                new_p[i][j] = new_p[i][j+1] = 'U'
                new_p[i+1][j] = new_p[i+1][j+1] = 'D'
                tag = hash(new_p)
                if tag not in done:
                    done.add(tag)
                    to_do.append((new_p, i, j))
            if paving[i][j] == 'U' and paving[i][j+1] == 'U' and paving[i+1][j] == 'D' and paving[i+1][j+1] == 'D':
                new_p = [paving[k] if k not in (i, i+1) else paving[k][:] for k in range(h)]
                new_p[i][j] = new_p[i+1][j] = 'L'
                new_p[i][j+1] = new_p[i+1][j+1] = 'R'
                tag = hash(new_p)
                if tag not in done:
                    done.add(tag)
                    to_do.append((new_p, i, j))

print(len(done))
