#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int main() {
  char code[6][6] = {"ABCDE", "JIHGF", "KLMNO", "TSRZP", "UVWXY"};
  int N;
  scanf("%d", &N);
  fgetc(stdin);
  for (int i = 0; i < N; i++) {
    char LINE[101];
    scanf("%[^\n]", LINE);
    fgetc(stdin);
    char output_line[30];
    memset(output_line, '\0', sizeof(output_line));
    char *token;
    token = strtok(LINE, " ");
    while (token != NULL) {
      int y = token[0] - '0';
      int x = token[1] - '0';
      char decode[2];
      decode[0] = code[x][y];
      strcat(output_line, decode);
      token = strtok(NULL, " ");
    }
    for (int j = 1; j < strlen(output_line); j++) {
      if (output_line[j] == 'U')
        if (output_line[j - 1] == 'U')
          output_line[j - 1] = 'Q';
    }
    printf("%s\n", output_line);
  }

  return 0;
}
