#include <iostream>
#include <string>
#include <vector>
#include <algorithm>
#include <map>
using namespace std;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/

class Trade
{
    public:
    string symbol;
    int qty;
    float price;
    Trade()
    {
        symbol="";
        qty=0;
    };
};

class Order
{
    public:
    int seqNo;
    int qty;
    float price;
};

class Book
{
    public:
    int lastSeqNo;
    string symbol;
    vector<Order> buy;
    vector<Order> sell;
    Book()
    {
        lastSeqNo=0;
        symbol="";
    };
    Trade addOrder(string verb,int qty, float price);
};
bool compareSell (Order &i,Order &j) { if (i.price == j.price ) return (i.seqNo<j.seqNo); else return (i.price <=j.price); }
bool compareBuy  (Order &i,Order &j) { if (i.price == j.price ) return (i.seqNo<j.seqNo); else return (i.price >=j.price); }
Trade Book::addOrder(string verb,int qty, float price)
{
    Trade res;
    vector<Order>::iterator it;
    if(verb=="SELL")
    {
        if (!buy.empty() && buy[0].price>= price)
        {
            res.symbol=symbol;
            it = buy.begin();
            res.qty=min(it->qty,qty);
            it->qty-=res.qty;
            res.price=it->price;
            if (it->qty==0)
                buy.erase(buy.begin());
        }
        else
        {
            Order order;
            order.qty=qty;
            order.price=price;
            order.seqNo=lastSeqNo;
            lastSeqNo++;
            sell.push_back(order);
            sort(sell.begin(),sell.end(), compareSell);
        }

    }
    else
    {
        if (!sell.empty() && sell[0].price<= price)
        {
            res.symbol=symbol;
            it = sell.begin();
            res.qty=min(it->qty,qty);
            it->qty-=res.qty;
            res.price=it->price;
            if (it->qty==0)
                sell.erase(sell.begin());
        }
        else
        {
            Order order;
            order.qty=qty;
            order.price=price;
            order.seqNo=lastSeqNo;
            lastSeqNo++;
            buy.push_back(order);
            sort(buy.begin(),buy.end(), compareBuy);
        }

    }
    return res;
}
void cerrBook(Book b)
{
    cerr<<b.symbol<<endl;
    cerr<<"BUY"<<endl;
    for (int i=0;i<b.buy.size();i++)
        cerr<<b.buy[i].seqNo<<":"<<b.buy[i].qty<<"@"<<b.buy[i].price<<endl;
    cerr<<"SELL"<<endl;
    for (int i=0;i<b.sell.size();i++)
        cerr<<b.sell[i].seqNo<<":"<<b.sell[i].qty<<"@"<<b.sell[i].price<<endl;
}



int main()
{
    map<string,Book> books;
    map<string,Book>::iterator it;
    int N;
    cin >> N; cin.ignore();
    int nbTrades=0;
    cout.setf(cout.fixed);
    cout.precision(2);
    for (int i = 0; i < N; i++) {
        string symbol;
        string verb;
        int qty;
        float price;
        cin >> symbol >> verb >> qty >> price; cin.ignore();

        if ( books.find(symbol)==books.end())
        {
            books[symbol].symbol=symbol;
        }        
        Trade t;
        do
        {
            t= books[symbol].addOrder(verb,qty,price);
            if (t.qty>0)
            {
                cout<<t.symbol<<" "<<t.qty<<" "<<t.price<<endl;
                nbTrades++;
                qty-=t.qty;
            }
//            cerrBook(books[symbol]);
        } while (t.qty>0 && qty);
    }


    if (!nbTrades)
        cout << "NO TRADE" << endl;
}
