using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;

public class Program
{
	public const string end = "end";
	public const string start = "start";

	public static void Main()
	{
        //plural
        Measurement dist = new Measurement("miles 8 furlongs 10 chains 22 yards 3 feet 12 inches");

        //singular
        Measurement time = new Measurement("fortnight 2 week 7 day 24 hour 60 minute 60 second");

        string inputString = Console.ReadLine();

        var inputRay = inputString.Split(' ');

        int indexPer1 = Array.IndexOf(inputRay, "per"); inputRay[indexPer1] = "removePer"; 
        int indexPer2 = Array.IndexOf(inputRay, "per");

        double inputNumber = Convert.ToDouble(inputRay[indexPer1-2]);

        string distA = (inputRay[indexPer1-1]);
        string distZ = (inputRay[indexPer2-1]);

        string timeA = (inputRay[indexPer1+1]);
        string timeZ = (inputRay[indexPer2+1]);

        dist.SetIndex(start, distA); dist.SetIndex(end, distZ); 
        time.SetIndex(start, timeA); time.SetIndex(end, timeZ);	

        dist.CalculateMultDiv();
        time.CalculateMultDiv();

        double ans = inputNumber * dist.TheMultDiv / time.TheMultDiv;
        double ansROUND = Math.Round(ans,1);

        Console.WriteLine($"{ansROUND.ToString("0.0")} {distZ} per {timeZ}");	
	}

	public class Measurement
	{
		public List<string> Terms = new List<string>();
		public List<int> MultDivs = new List<int>();
		public int StartIndex;
		public int EndIndex;
		public double TheMultDiv;

		public Measurement(string allTermsAndMultDivs)
		{
			var inputRay = allTermsAndMultDivs.Split(' ');
            Array.Reverse(inputRay);
			for (int index = 0 ; index < inputRay.Length ; index++)
			{
				string item = inputRay[index];
                if (index % 2 == 0) Terms.Add(item); 
				if (index % 2 == 1) MultDivs.Add(int.Parse(item));				
			}
		}

		public void SetIndex (string startOrEnd, string inputTerm)
		{
			for (int i = 0 ; i < Terms.Count ; i++)
			{
				string aTerm = Terms[i];
				if (aTerm == inputTerm)
				{
					if (startOrEnd == start) StartIndex = i;
					if (startOrEnd == end) EndIndex = i;
					return;
				}
			}
		}

		public void CalculateMultDiv()
		{
			double output = 1; 		

			bool inputIsSmallerUnitsThanOutput = StartIndex < EndIndex;

			if (inputIsSmallerUnitsThanOutput)
			{
				for (int i = StartIndex ; i < EndIndex ; i++)
				{
					output /= MultDivs[i];
				}
			}
			else
			{
				for (int i = StartIndex ; i > EndIndex ; i--)
				{
					output *= MultDivs[i-1];
				}
			}
			TheMultDiv = output;
		}		
	}
}
