#include <iostream>
#include <vector>
using namespace std;
int main(){
  int N,f=1,res=0,lev;
  cin >> N; cin.ignore();
  vector<vector<int>> water(N,vector<int> (N,0)),pool=water;
  for (int i = 0; i < N; i++) {
    string s;
    getline(cin, s);
    for (int j = 0; j < N; j++) {
      pool[i][j]=s[j]-'A';
      water[i][j]=26;
      if(i==0 || j==0 || i==N-1 || j==N-1){water[i][j]=pool[i][j];}}}
  while(f){
    f=0;
    for (int i = 1; i < N-1; i++) {
      for (int j = 1; j < N-1; j++) {
        lev=max(pool[i][j],min(min(water[i+1][j],water[i-1][j]),min(water[i][j+1],water[i][j-1])));
        if(lev<water[i][j]){water[i][j]=lev;f=1;}}}}
  for (int i = 1; i < N-1; i++) {
    for (int j = 1; j < N-1; j++) {
      res+=water[i][j]-pool[i][j];}}
  cout<<res;}
