using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;
using System.Text.RegularExpressions;

//Source: https://nameberry.com/list/240/cool-girl-names-from-ancient-cultures/all

public class Program
{
	public static void Main()
	{
        string responses = Console.ReadLine();
        string input = responses;

        int f = Regex.Matches(input, "is free").Count;
        int nf = Regex.Matches(input, "is not free").Count;
        double perc = 100.0 * f/(f+nf);
        double percRound = Math.Round(perc,0);
        string bl = " ";
        Console.WriteLine(f + bl + percRound + "%");
	}
}
