import java.util.Scanner;

// Standard long solution
class Solution {

    public static void main(String args[]) {
        Scanner scanner = new Scanner(System.in);
        String program = scanner.nextLine();

        int cells[] = new int[10000];
        int currentIndex = 0;
        for (Character command : program.toCharArray()) {
            switch (command) {
                case '>':
                    currentIndex++;
                    if (currentIndex == 10000) {
                        currentIndex = 0;
                    }
                    break;
                case '<':
                    currentIndex--;
                    if (currentIndex == -1) {
                        currentIndex = 9999;
                    }
                    break;
                case '+':
                    cells[currentIndex]++;
                    break;
                case '-':
                    cells[currentIndex]--;
                    break;
            }
        }

        int sum = 0;
        for (int i = 0; i < cells.length; i++) {
            if (cells[i] > 0) {
                sum += cells[i];
            }
        }

        System.out.println(sum);
    }

}
