w = input()
c = input()

reverse_w = w[::-1]


if c == "S":
    ans = []
    for i in range(len(w)):
        if i % 2 == 0:
            ans.append(w)
        else:
            ans.append(reverse_w)
    print(" ".join(ans))
else:
    for i in range(len(w)):
        if i % 2 == 0:
            print(w)
        else:
            print(reverse_w)
