data.solution:
a=input().split(':')
b=input().split(' -> ')
if len(b)==1:b=b[0].split(' <- ');tmp=b[1];b[1]=b[0];b[0]=tmp
if b[0]=='Analog':a=map(int,a)
elif b[0]=='Baran':a=map(lambda x:int(x,2),a)
else:a=map(lambda x:int(x,16),a)
if b[1]=='Analog':print(':'.join(map(lambda x:f'{x:02d}',a)))
elif b[1]=='Baran':print(':'.join(map(lambda x:f'{x:06b}',a))[1:])
else:print(':'.join(map(lambda r:f'{r:02x}'.upper(),a)))
