using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;
/*
15 human years equals the first year of a medium-sized dog’s life.
Year two for a dog equals about nine years for a human.
And after that, each human year would be approximately five years for a dog.
https://www.vetstreet.com/our-pet-experts/11-pet-names-that-are-a-real-mouthful
https://www.dailypaws.com/dogs-puppies/dog-names/funny-dog-names
*/
public class Program
{
    public static string bl = " ";
	public static void Main()
	{
        string input = Console.ReadLine();
        var Ray = input.Split(' ');
        int indexIs = Array.IndexOf(Ray,"is");

        string Name = ConcateName(Ray, indexIs);

        int indexYear = FindLocationOfAnyVersionOfThisWord(Ray,"year");
        int indexMonth = FindLocationOfAnyVersionOfThisWord(Ray,"month");

        int ageYear  = indexYear == -1 ? 0 : int.Parse(Ray[indexYear - 1]); 
        int ageMonth = indexMonth == -1 ? 0 : int.Parse(Ray[indexMonth - 1]);
        /*
        • year 1 = 15
        • year 2 = 9
        • each = 5 
        */

        double total = 0;
        if (ageYear == 0 ) 
        {
            total = ConvertDogMonthsToHumanYears(ageMonth, 15); 
        }

        if (ageYear == 1)
        {
            total = 15;
            total += ConvertDogMonthsToHumanYears(ageMonth, 9); 
        }

        if (ageYear >= 2)
        {
            int addlYears = ageYear - 2; 
            total = 15 + 9 + (5 * addlYears);
            total += ConvertDogMonthsToHumanYears(ageMonth, 5); 
        }

        total = Math.Round(total,0);
        Console.WriteLine($"{Name} is like a {total}-year-old-person");
	}

	public static string ConcateName(string[] ray, int index)
	{
		string fullName = "";
		string bl = " ";
		for (int i = 0 ; i < index ; i++)
		{
			fullName += ray[i] + bl;
		}
		return fullName.Trim();
	}

	public static double ConvertDogMonthsToHumanYears(int months, int conversion)
	{
		double output = 1.0 * months/12 * conversion;
		return output;
	}

	public static int FindLocationOfAnyVersionOfThisWord (string[] ray, string word)
	{
		for (int i = 0 ; i < ray.Length ; i++)
		{
			if (ray[i] == word)	return i;
			if (ray[i] == word + "s") return i;
		}
		return -1;
	}
}
