package main

import (
	"fmt"
	"strings"
	"bufio"
	"os"
)

const (
	U Orient = Orient(iota)
	L
	R
	F
	B
	D
)

type Orient int

func (o Orient) String() string {
	if(o>5){
		panic("not a face")
	}
	return string("ULRFBD"[o])
}

type Cube struct{
	faces []Orient
}

func (c Cube) String() string {
	return fmt.Sprint(c.faces)
}

func newCube() Cube {
	return Cube{faces:[]Orient{Orient(U),Orient(L),Orient(R),Orient(F),Orient(B),Orient(D)}}
}

var rotX map[Orient]Orient = map[Orient]Orient{U:B,B:D,D:F,F:U}
var rotXI map[Orient]Orient = map[Orient]Orient{B:U,D:B,F:D,U:F}

var rotY map[Orient]Orient = map[Orient]Orient{F:L,L:B,B:R,R:F}
var rotYI map[Orient]Orient = map[Orient]Orient{L:F,B:L,R:B,F:R}

var rotZ map[Orient]Orient = map[Orient]Orient{U:R,R:D,D:L,L:U}
var rotZI map[Orient]Orient = map[Orient]Orient{R:U,D:R,L:D,U:L}

func (c *Cube) rot(axMap map[Orient]Orient){
	for i,face:=range c.faces {
		if nf,ok:=axMap[face]; ok {
			c.faces[i]=nf
		}
	}
}

func (c *Cube) RotX(){
	c.rot(rotX)
}

func (c *Cube) RotY(){
	c.rot(rotY)
}

func (c *Cube) RotZ(){
	c.rot(rotZ)
}

func (c *Cube) RotXI(){
	c.rot(rotXI)
}

func (c *Cube) RotYI(){
	c.rot(rotYI)
}

func (c *Cube) RotZI(){
	c.rot(rotZI)
}



func main() {
	var s=bufio.NewScanner(os.Stdin)
	s.Scan()
	splits:=strings.Split(s.Text()," ")
	var cube Cube=newCube()
	for _,split := range splits {
		switch split {
		case "x":
			cube.RotX()
		case "y":
			cube.RotY()
		case "z":
			cube.RotZ()
		case "x'":
			cube.RotXI()
		case "y'":
			cube.RotYI()
		case "z'":
			cube.RotZI()
		}
	}
	s.Scan()
	fmt.Println(cube.faces[strings.Index("ULRFBD",s.Text())])
	s.Scan()
	fmt.Println(cube.faces[strings.Index("ULRFBD",s.Text())])
}
