#include <iostream>
#include <algorithm>
#include <string>
#include <vector>
#include <cmath>

using namespace std; 


int main() {

    vector<string>letters = {"a","b","c","d","e","f","g","h","i","j","k","l","m","n","o","p","q","r","s","t","u","v","w","x","y","z"}; // list of letters 

    string input; 
    cin>>input; // the string that is checked
    int len=input.length(); // lenght of the string 
    int count=0; 

    for(int i=0; i<len; i++)
    {
        input[i] = tolower(input[i]);// makes all the inputs lowercase
    }
    vector<string>location(len); // the contents of the input stored in a vector array 

    for(int i=0; i<len; i++)
    {
        location[i] = input[i]; // here we assign the individual values 
    }
    int n=0; // this is the sum variable 

    for(int j=0; j<len; j++)
    {
        for(int i=0; i<26; i++)
        {
            if(letters[i]==location[j])
            {
                n+=(i+1); // summing up the letter positions
            }
        }
    }
    cout<<n<<endl; // outputs sum
    int div = 0; 
    int opp=0;

    if(n%2==0) // if the sum is even 
    {
        div=1;
        opp=0;
    }
    else{ // if it is false 
        div=0;
        opp=1;
    }

    vector<int>nums;
    int i=0;
    while (n != 0) // changes eeverything to biinary representation
    {
        nums.push_back(n % 2);
        i++;
        n = n / 2;
    }
    reverse(nums.begin(),nums.end()); // puts binary in correct order 

    for(auto x:nums)
    {
        cout<<x; // outputs binary 
    }
    cout<<endl;

    int max=0, acc = 0;

    for(int i=0; i<nums.size(); i++)
    {
        if(nums[i]!=div)
        {
            acc = 0;
        }
        else{
            acc++;
        }

        if (max < acc) {
            // this gets maximum consecutive 1's or 0's 
            max = acc;
        }
    }

    cout << max <<endl; // maximum consecutive 1's or 0's 



}
