using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;

public class Program
{
	public static char[,] Domi;
	public static int HI;   public static int WI;
	public static int biHI; public static int biWI; // biggest Indexes
	public static int midIndex;
	public static char pipe = '|';
	public static char dash = '-';
	public static char plus = '+';
	public static char ast = '*';
	public static Dictionary<int,string> dic;
	public static void Main()
	{
		HI = 9;
		WI = 5;
		biHI = HI - 1;
		biWI = WI - 1;

        midIndex = HI / 2;

        dic = new Dictionary<int,string>();
		dic.Add(0, "xxxxxxxxx");
		dic.Add(1, "xxxx0xxxx");
		dic.Add(2, "0xxxxxxx0");
		dic.Add(3, "0xxx0xxx0");
		dic.Add(4, "0x0xxx0x0");
		dic.Add(5, "0x0x0x0x0");
		dic.Add(6, "0x00x00x0");

		Domi = new char[HI, WI];
		PopulateDomi();

        string[] inputs = Console.ReadLine().Split(' ');
        int topNumber = int.Parse(inputs[0]);
        int bottomNumber = int.Parse(inputs[1]);

        PopulateASquare(topNumber,1);
        PopulateASquare(bottomNumber,2);

        PrintDomi();
	}


	public static void PopulateASquare(int num, int whichSquare)	
	{
		string numberString = dic[num];
		int startPos = 1;
		int startLin = 1 + midIndex * (whichSquare - 1);
		for (int i = 0 ; i < 9 ; i++)
		{
			int lin = startLin + i / 3;
			int pos = startPos + i % 3;
			if (numberString[i] == '0')
			{
				Domi[lin,pos] = ast;
			}
		}
	}

	public static void PrintDomi()
	{
		for (int h = 0 ; h < HI ; h++)
		{
			string aLine = "";
			for (int w = 0 ; w < WI ; w++)
			{
				aLine += Domi[h,w];
			}
			Console.WriteLine(aLine);
		}
	}

	public static void PopulateDomi()
	{
		for (int h = 0 ; h < HI ; h++)
		{
			for (int w = 0 ; w < WI ; w++)
			{
				char ch = ' ';
				bool topBotMid = h == 0 || h == biHI || h == midIndex;
				bool sides = w == 0 || w == biWI;

				if (sides) ch = pipe;
				if (topBotMid) ch = dash;
				if (sides && topBotMid) ch = plus;

				Domi[h,w] = ch;
			}
		}
	}
}
