html=input()

valid_tags = ["html", "body", "head", "div"]
open_tags =[]
tag_count = 0
tag = ""

while html:
    tag_end = html.find('>')

    if html.startswith("</"):
        tag = html[2:tag_end]

        if open_tags and tag == open_tags[-1]:
            open_tags.pop(-1)
        else:
            print("Broken")
            exit()

        html=html[2+ len(tag):]

    elif html.startswith("<"):
        tag = html[1:tag_end]


        if '<' in tag:
            html = html[1:]

        elif tag in valid_tags:
            open_tags.append(tag)
            tag_count += 1
            html = html[1 + len(tag):]

        else:
            print("Broken")
            exit()

    else:
        html = html[1:]

print(tag_count if not open_tags else "Broken")
