#include <bits/stdc++.h>
using namespace std;

int main() {
    int total;
    int width;
    int center;
    cin >> total >> width >> center; cin.ignore();
    if (width / 2 + 1 > center) {
        for (int i = 1; i <= width; i++) {
            cout << i << (i == width ? '\n' : ' ');
        }
    } else if (total - width / 2 < center) {
        for (int i = total - width + 1; i <= total; i++) {
            cout << i << (i == total ? '\n' : ' ');
        }
    } else {
        for (int i = center - width / 2; i <= center + width / 2; i++) {
            cout << i << (i == center + width / 2 ? '\n' : ' ');
        }
    }
}
