data.solution:
n = int(input())
prev_score = 0
score_to_points = {'0':0, '15':1,'30':2,'40':3}
score_list = [input() for i in range(n)]
points_serve, points_return = 0,0
if 'deuce' not in score_list:
    last_point_serve, last_point_return = [s for s in score_list[n-2].split('-')]
    points_serve, points_return = score_to_points[last_point_serve], score_to_points[last_point_return]
    if points_serve > points_return:
        points_serve += 1
    else:
        points_return += 1
else:
    deuces = score_list.count('deuce')
    last_deuce_score = deuces+2 
    last_score = score_list[n-2]
    if 'in' in last_score:
        points_serve, points_return = last_deuce_score+2, last_deuce_score
    else:
        points_serve, points_return = last_deuce_score, last_deuce_score+2
print(f'{points_serve}-{points_return}')
