import sys

string = input().strip()
limit = int(input())

def compress_string(string, limit):
    compressed = ""
    count = 1

    for i in range(1, len(string)):
        if string[i] == string[i-1]:
            count += 1
        else:
            if count > limit:
                compressed += string[i-1] + str(count)
            else:
                compressed += string[i-1] * count
            count = 1

    # Handle the last character sequence
    if count > limit:
        compressed += string[-1] + str(count)
    else:
        compressed += string[-1] * count
    return compressed

compressed_string = compress_string(string, limit)
print(compressed_string)
