substitution_mode, start_point = input().split()
k = int(input())
keywords = [word for word in input().split()]
verse = input()

if substitution_mode == 'WORDS':
    n = k
else:
    keys = []
    for word in keywords:
        keys += [*word]
    keywords = keys

    n = len(keywords)

if start_point == 'BACK':
    keywords.reverse()

print(verse) # first, the original verse
for i in range(n):
    key = keywords.pop(0)

    if start_point == 'BACK':
        verse = verse[::-1].replace(key[::-1], '_', 1)[::-1]
    else:
        verse = verse.replace(key, '_', 1)

    print(verse) # the verse with the next replacement
