#include <iostream>
#include <vector>
#include <algorithm>
using namespace std;
int main(){
  int N,W;
  cin>>N>>W;
  string s,x;
  vector<string> S(2);
  for(int i=0;i<W;i++){
    cin>>x;
    s+=x;}
  for(int i=0;i<s.size();i+=N+1)S[0]+=s[i];
  for(int i=N-1;i<s.size()-1;i+=N-1)S[1]+=s[i];
  sort(S.begin(),S.end());
  cout<<S[0]<<" "<<S[1];}
