#include <bits/stdc++.h>
using namespace std;
int main(){
  int N;
  cin >> N; cin.ignore();
  vector<pair<double,string>> P(N);
  for (int i = 0; i < N; i++) {
    int PA,S,D,T,HR,W,HBP,SAC;
    cin>>P[i].second>>PA>>S>>D>>T>>HR>>W>>HBP>>SAC;
    P[i].first=round(1000.0*double(S+D+T+HR)/(PA-W-HBP-SAC));}
  sort(P.rbegin(),P.rend());
  for (int i = 0; i < N; i++) {
    cout <<P[i].second<<" ."<<(int)P[i].first<< endl;}}
