const time = parseInt(readline());
const loadingTime = parseInt(readline());
const video = parseInt(readline());
const ads = parseInt(readline());
let vidTime = loadingTime + video
if(vidTime > time) return console.log('A ticket to Heaven\nI still hate ads')
for (let i = 0; i < ads; i++) {
   var inputs = readline().split(' ');
   const adLength = parseInt(inputs[0]);
   const skippable = parseInt(inputs[1]);
   const skipTime = parseInt(inputs[2]);
   if(skippable === 0) {
        vidTime += adLength
    } else {
        vidTime += skipTime
    }
}
console.log(vidTime > time ? 'A ticket to Heaven\nAds kill' : 'Stay on Earth\nAds are still annoying');
