n = int(input())
field = [list()] * n
for i in range(n):
    field[i] = input().split()

for i in range(n):
    for j in range(n):
        if field[i][j] == 'o':
            field[i][j] = 0
            for k in [i - 1, i, i + 1]:
                for m in [j - 1, j, j + 1]:
                    if 0 <= k <= n - 1 and 0 <= m <= n - 1:
                        if field[k][m] == 'b':
                            field[i][j] += 1

for i in range(n):
    print(*field[i])
