cuteness = readline().split(" ")
const n = cuteness.length;

if (n < 2) {
  console.log("-1 -1");
  return
}

let maxCuteness = -Infinity;
let cutestPairIndices = [-1, -1];

for (let i = 0; i < n; i++) {
  for (let j = i + 1; j < n; j++) {
    if (cuteness[i] * cuteness[j] > 0) {
        const pony1Cuteness = Math.abs(cuteness[i]);
        const pony2Cuteness = Math.abs(cuteness[j]);
        const totalCuteness = pony1Cuteness + pony2Cuteness;

        if (totalCuteness > maxCuteness || (totalCuteness === maxCuteness && i < cutestPairIndices[0])) {
            maxCuteness = totalCuteness;
            cutestPairIndices = [i, j];
          }
    }
  }
}

console.log(cutestPairIndices[0], cutestPairIndices[1])
